// Equipment Items Database - Auto-generated from design doc
// ~170 items across 10 equipment slots and 6 rarity tiers

export type EquipmentSlot = "head" | "chest" | "hands" | "boots" | "cloak" | "ring" | "necklace" | "mainHand" | "offHand" | "book" | "collar" | "restraints" | "toy";
export type EquipedSlot = "head" | "chest" | "hands" | "boots" | "cloak" | "ring1" | "ring2" | "necklace" | "mainHand" | "offHand" | "book" | "collar" | "restraints" | "toy";
export type Rarity = "mundane" | "common" | "uncommon" | "rare" | "epic" | "legendary";

export type BindingRule = "boe" | "bop" | "none";

export interface EquipmentItem {
  id: string;
  name: string;
  nameFr?: string; // Deprecated, kept for compatibility
  description: string;
  type: EquipmentSlot;
  rarity: Rarity;
  bindingRule?: BindingRule; // bop = legendary, boe = epic, none = everything else
  stats: {
    ac?: number;
    hp?: number;
    speed?: number;
    damage?: string;
    strength?: number;
    dexterity?: number;
    constitution?: number;
    intelligence?: number;
    wisdom?: number;
    charisma?: number;
  };
  specialAttributes?: {
    critChance?: number;
    damageBonus?: number;
    spellPower?: number;
    healingPower?: number;
    stealthBonus?: number;
    perceptionBonus?: number;
    persuasionBonus?: number;
    xpBonus?: number;
    seduction?: number;
    intimidation?: number;
    submission?: number;
    bondageArts?: number;
    sensationMastery?: number;
    trustBuilding?: number;
    aftercarePower?: number;
    painThreshold?: number;
    allure?: number;
    commandPresence?: number;
  };
  passive?: string;
}

